import {useState} from 'react';

function Square({ value, onSquareClick }) {
  return (
   <button className="square" onClick={onSquareClick}>
    {value}
   </button>
  );
}

function Board({ xIsNext, squares, onPlay }) {
  function handleClick(i) {
    if (squares[i] || calculateWinner(squares)) {
      return;
    }
    const nextSquares = squares.slice();
    if (xIsNext) {
      nextSquares[i] = 'X';
    } else {
      nextSquares[i] = 'O';
    }
    onPlay(nextSquares);
  }

  const winner = calculateWinner(squares);
  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? 'X' : 'O')
  }


  function buildBoard() {
    const numRows = 3;
    const numCols = 3;
    const board = [];

    board.push(<div key={"status"} className="status">{status}</div>)
    for (let row = 0; row < numRows; row++) {
      board.push(<div key={"row-"+row+1} className="board-row"></div>)
      for (let col = 0; col < numCols; col++) {
        const squareNum = col + 3*row;
        board.push(<Square key={"square-"+(squareNum+1)} value={squares[squareNum]} onSquareClick={() => handleClick(squareNum)} />);
      }
    } 

    return board;
  }

  return <>{buildBoard()}</>;
}

export default function Game() {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);
  const xIsNext = currentMove % 2 === 0;
  const currentSquares = history[currentMove];

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move > 0) {
      if (move === currentMove) {
        description = "You are at move #" + move;
        return (
          <li key={move}>
            <div className="description">{description}</div>   
          </li>
        );
      } else {
        description = "Go to move #" + move;
      }
    } else {
      description = "Go to game start";
    }

    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });

  const [sortedMoves, setSortedMoves] = useState(moves);

  function sortMoves() {
    sortedMoves.reverse();
    setSortedMoves(sortedMoves);
  }

  return (
    <div className='game'>
      <div className='game-board'>
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} />
      </div>
      <div className='game-info'>
        <div>
          <button onClick={() => sortMoves()}>{"Sort Ascending/Descending"}</button>
        </div>
        <ol>{moves}</ol>
      </div>
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
